(function(){$(document).ready(function(){var b=$(".edit-panel");b.hide();var a=$(".edit-label");a.css("cursor","pointer");a.hover(function(){$(this).css("color","#333")},function(){$(this).css("color","")});a.click(function(){var c=$(this);if(c.siblings(".edit-panel").is(":hidden")){c.closest(".sandbox").siblings(".sandbox").find(".edit-panel:visible").slideUp();c.siblings(".edit-panel").slideDown()}else{c.siblings(".edit-panel").slideUp()}});b.each(function(){var c=$(this);if(c.find(".message").length){c.show()}})})})();